import { useState } from "react";
// import Todo from "./Todo";

function TodoForm() {
  const [changes, setChanges] = useState("");
  const [todos, setTodos] = useState([]);
  const [editTodo, setEditTodo] = useState("");
  const [show, setShow] = useState(false);
  const [updte, setUpdate] = useState('')

  console.log(editTodo);

  //   console.log(changes);

  const handleSubmit = () => {
    if (changes !== "") {
      setTodos((prev) => [...prev, changes]);
      setChanges("");
    }
  };

  const handleDel = (index) => {
    setTodos(todos.filter((_, index1) => index1 !== index)); //index1=[0, 1, 2, 3,...]  index= vị trí click
  };

  const handleEdit = (todo, index) => {
    setShow(true);
    // const temp = todos.splice(index, 1);
    // console.log(todo);
    setEditTodo(todo, index);

    // setTodos(temp)
    // todo= false;
  };
  const handleUpdateTodo = (todo) => {
    // console.log("a")
    // const newTodos = [...todos];
    // const index = newTodos.findIndex( (element) => element === editTodo);
    // newTodos.splice(index, 1, editTodo);
    setUpdate((prev) => [...prev, editTodo]);
    setTodos(todos.filter((item) => item!==todos));
    console.log(editTodo);
    console.log(todos);
    console.log(update);
    // setTodos(todos.splice(index, 1, editTodo));
    setShow(false)
  };

  return (
    <div>
      <div>
        <input
          className="todo-input"
          value={changes}
          onChange={(e) => setChanges(e.target.value)}
        />
        <button className="todo-button" onClick={handleSubmit}>
          Add To Do
        </button>
      </div>
      {/* {todos.map(
        (todo, index) =>
          show && (
            <Todo
              todoProp={todo}
              key={index}
              onChangeForm={handleUpdateTodo}
              index={index}
            />
          )
      )} */}

      {show && (
        <>
          <input value={editTodo} onChange={(e)=>setEditTodo(e.target.value)} />
          <button onClick={handleUpdateTodo}>update</button>
        </>
      )}
      <ul>
        {!show &&
          todos.map((todo, index) => (
            <li key={index}>
              {todo}
              <button className="del-button" onClick={() => handleDel(index)}>
                Del
              </button>
              <button onClick={() => handleEdit(todo, index)}>Edit</button>
            </li>
          ))}
      </ul>
    </div>
  );
}

export default TodoForm;
