import { useState } from "react";

function Todo({ prop, onChangeForm }) {
  const [update, setUpdate] = useState(todo);

  const handleUpdate = () => {
    if (onChangeForm(update));
  };

  return (
    <div>
      <input
        className="todo-input"
        value={update}
        onChange={(e) => setUpdate(e.target.value)}
      />
      <button className="todo-button" onClick={handleUpdate}>
        Update
      </button>
    </div>
  );
}

export default Todo;
