import { useState } from "react";
import Todo from "./Todo";

function TodoForm() {
  const [changes, setChanges] = useState("");
  const [todos, setTodos] = useState(["dinh", "dien", "huy"]);
  const [edit, setEdit] = useState();

  //   console.log(changes);

  const handleSubmit = () => {
    if (changes !== "") {
      setTodos((prev) => [...prev, changes]);
      setChanges("");
    }
  };

  const handleDel = () => {
    setTodos([]);
  };

  const handleUpdateTodo = (value, index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1, value)
    setTodos(newTodos);
  };

  return (
    <div>
      <div>
        <input
          className="todo-input"
          value={changes}
          onChange={(e) => setChanges(e.target.value)}
        />
        <button className="todo-button" onClick={handleSubmit}>
          Add To Do
        </button>
      </div>
      <ul>
        {todos.map((todo, index) => (
          <Todo
            todo={todo}
            key={index}
            onChangeForm={handleUpdateTodo}
            index={index}
          />
          // <li key={index}>
          //   {todo}
          //   <button className="del-button" onClick={handleDel}>
          //     Del
          //   </button>
          //   <button onClick={handleEdit}>
          //       Edit</button>
          // </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoForm;
