import { useState } from "react";
import Todo from "./Todo";

function TodoForm() {
  const [changes, setChanges] = useState("");
  const [todos, setTodos] = useState([]);
  const [show, setShow] = useState(false);

  //   console.log(changes);

  const handleSubmit = () => {
    if (changes !== "") {
      setTodos((prev) => [...prev, changes]);
      setChanges("");
    }
  };

  const handleDel = (index) => {
    setTodos(todos.filter((_, index1) => index1 !== index)); //index1=[0, 1, 2, 3,...]  index= vị trí click
  };

  const handleEdit = (todo) => {
    setShow(!show);
    // const temp = todos.splice(index, 1);

    // setTodos(temp)
    // todo= false;
  };
  const handleUpdateTodo = (value, index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1, value);
    setTodos(newTodos);
  };

  return (
    <div>
      <div>
        <input
          className="todo-input"
          value={changes}
          onChange={(e) => setChanges(e.target.value)}
        />
        <button className="todo-button" onClick={handleSubmit}>
          Add To Do
        </button>
      </div>
      {todos.map(
        (todo, index) =>
          show && (
            <Todo
              todoProp={todo}
              key={index}
              onChangeForm={handleUpdateTodo}
              index={index}
            />
          )
      )}
      <ul>
        {todos.map((todo, index) => (
          <li key={index}>
            {todo}
            <button className="del-button" onClick={() => handleDel(index)}>
              Del
            </button>
            <button onClick={()=>handleEdit(todo)}>Edit</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoForm;
